How can I deploy a contract on Foundry which takes a struct as an argument in the constructor?
A: This can be achieved as follows:
contract A {
  struct Config {
    address someConfig;
    address anotherConfig;
  }
  Config config;
  constructor(Config memory param) {
    config = param;
  }
}

contract Test {
  A a;
  function setup() {
    a = new A(
      A.Config({
        someConfig: address(0x001),
        anotherConfig: address(0x002)
      })
    );
  }
}
